import React, { useState } from 'react';
import { signUp, signIn, signOut, getCurrentUser } from 'aws-amplify/auth';
import countries from '../data/countries.json';

function Auth() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
    birthdate: '',
    gender: '',
    nationality: '',
    allergies: ''
  });
  const [user, setUser] = useState(null);

  const handleSignUp = async (e) => {
    e.preventDefault();
    try {
      const { user } = await signUp({
        username: formData.username,
        password: formData.password,
        attributes: {
          email: formData.email,
          birthdate: formData.birthdate,
          gender: formData.gender,
          preferred_username: formData.username,
          'custom:nationality': formData.nationality,
          'custom:allergies': formData.allergies
        }
      });
      console.log('Sign up success:', user);
      alert('Sign up successful!');
    } catch (error) {
      console.error('Sign up error:', error);
      alert('Sign up failed: ' + error.message);
    }
  };

  const handleSignIn = async (e) => {
    e.preventDefault();
    try {
      const user = await signIn({
        username: formData.username,
        password: formData.password
      });
      setUser(user);
    } catch (error) {
      console.error('Sign in error:', error);
      alert('Sign in failed: ' + error.message);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut();
      setUser(null);
    } catch (error) {
      console.error('Sign out error:', error);
    }
  };

  if (user) {
    return (
      <div style={{ padding: '20px' }}>
        <h2>Welcome!</h2>
        <button onClick={handleSignOut}>Sign Out</button>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '400px' }}>
      <h2>TDH Cook Social</h2>
      <form onSubmit={handleSignUp}>
        <input
          type="text"
          placeholder="Username"
          value={formData.username}
          onChange={(e) => setFormData({...formData, username: e.target.value})}
          required
          style={{ display: 'block', margin: '10px 0', padding: '8px', width: '100%' }}
        />
        <input
          type="password"
          placeholder="Password"
          value={formData.password}
          onChange={(e) => setFormData({...formData, password: e.target.value})}
          required
          style={{ display: 'block', margin: '10px 0', padding: '8px', width: '100%' }}
        />
        <input
          type="date"
          value={formData.birthdate}
          onChange={(e) => setFormData({...formData, birthdate: e.target.value})}
          required
          style={{ display: 'block', margin: '10px 0', padding: '8px', width: '100%' }}
        />
        <select
          value={formData.gender}
          onChange={(e) => setFormData({...formData, gender: e.target.value})}
          required
          style={{ display: 'block', margin: '10px 0', padding: '8px', width: '100%' }}
        >
          <option value="">Select Gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>
        <select
          value={formData.nationality}
          onChange={(e) => setFormData({...formData, nationality: e.target.value})}
          style={{ display: 'block', margin: '10px 0', padding: '8px', width: '100%' }}
        >
          <option value="">Nationality (optional)</option>
          {countries.map(country => (
            <option key={country.code} value={country.code}>
              {country.name}
            </option>
          ))}
        </select>
        <button type="submit" style={{ padding: '10px 20px', margin: '10px 0' }}>
          Sign Up
        </button>
      </form>
    </div>
  );
}

export default Auth;
